sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {

    var count = -1;
    var prefix = 'tableIN';
    var orGlobal = '^ORtable=global';

    describe('Count of UI', function() {

        describe('Form Action records', function() {

            var action = 'form_action';

            it('for Table x_402400_drink_drinks has not change', function() {
                var table = 'x_402400_drink_drinks';
                var encodedQuery = prefix + table + orGlobal;
                var expected = '23';
                uiActionExpectedCount(encodedQuery, action, expected);
            });

            it('for Table x_402400_drink_m2m_drinks_orders has not change', function() {
                var table = 'x_402400_drink_m2m_drinks_orders';
                var encodedQuery = prefix + table + orGlobal;
                var expected = '23';
                uiActionExpectedCount(encodedQuery, action, expected);
            });

            it('for Table x_402400_drink_orders has not change', function() {
                var table = 'x_402400_drink_orders';
                var orTask = '^ORtable=task';
                var encodedQuery = prefix + table + orTask + orGlobal;
                var expected = '35';
                uiActionExpectedCount(encodedQuery, action, expected);
            });
        });

        describe('List Action records', function() {

            var action = 'list_action';

            it('for Table x_402400_drink_orders has not change', function() {
                var table = 'x_402400_drink_orders';
                var orTaskCi = '^ORtable=task_ci';
                var encodedQuery = prefix + table + orTaskCi + orGlobal;
                var expected = '12';
                uiActionExpectedCount(encodedQuery, action, expected);
            });
        });
    });

    function uiActionExpectedCount(encodedQuery, action, expected) {
        var gaSysUiAction = new GlideAggregate('sys_ui_action');
        gaSysUiAction.addEncodedQuery(encodedQuery);
        gaSysUiAction.addQuery(action, '=', 'true');
        gaSysUiAction.addAggregate('COUNT');
        gaSysUiAction.query();
        if (gaSysUiAction.next()) {
            count = gaSysUiAction.getAggregate('COUNT');
        }
        expect(count).toBe(expected);
    }

})(outputs, steps, stepResult, assertEqual);
jasmine.getEnv().execute();
