sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {

    var count = -1;
    var applicationName = 'Drink';

    describe('Count of', function() {

        it('Tables in ' + applicationName + ' Application has not changed', function() {
            var gaSysDbObject = new GlideAggregate('sys_db_object');
            gaSysDbObject.addQuery('sys_scope.name', '=', applicationName);
            gaSysDbObject.addAggregate('COUNT');
            gaSysDbObject.query();
            var expected = '4';
            if (gaSysDbObject.next()) {
                count = gaSysDbObject.getAggregate('COUNT');
            }
            expect(count).toBe(expected);
        });

        describe('Fields in ', function() {

            it('Drink M2m Drinks Orders table has not change', function() {
                fieldExpectedCount('x_402400_drink_m2m_drinks_orders', '9');
            });

            it('Drink Sizes table has not change', function() {
                fieldExpectedCount('x_402400_drink_drink_sizes', '7');
            });

            it('Drinks table has not change', function() {
                fieldExpectedCount('x_402400_drink_drinks', '14');
            });

            it('Orders tables has not change', function() {
                fieldExpectedCount('x_402400_drink_orders', '3');
            });
        });
    });

    function fieldExpectedCount(table, expected) {
        var gaSysDictionary = new GlideAggregate('sys_dictionary');
        gaSysDictionary.addQuery('name', '=', table);
		gaSysDictionary.addNotNullQuery('element')
        gaSysDictionary.addAggregate('COUNT');
        gaSysDictionary.query();
        if (gaSysDictionary.next()) {
            count = gaSysDictionary.getAggregate('COUNT');
        }
        expect(count).toBe(expected);
    }

})(outputs, steps, stepResult, assertEqual);
jasmine.getEnv().randomizeTests(false);
jasmine.getEnv().execute();
