sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {
    describe('Count of fields', function() {

        it('tables in application has not changed', function() {
            var drinkApplicationSysId = '0cc0d4901b1101106c166464604bcbd2';
            var gaSysDbObject = new GlideAggregate('sys_db_object');
            gaSysDbObject.addQuery('sys_scope', '=', drinkApplicationSysId);
            gaSysDbObject.addAggregate('COUNT');
            gaSysDbObject.query();
            var expected = '4';
            var count = 0;
            if (gaSysDbObject.next()) {
                count = gaSysDbObject.getAggregate('COUNT');
            }
            expect(count).toBe(expected);
        });

        it('fields in Drink M2m Drinks Orders table has not change', function() {
            fieldsExpectedCount('x_402400_drink_m2m_drinks_orders', '10');
        });

        it('fields in Drink Sizes table has not change', function() {
            fieldsExpectedCount('x_402400_drink_drink_sizes', '8');
        });

        it('fields in Drinks table has not change', function() {
            fieldsExpectedCount('x_402400_drink_drinks', '15');
        });

        it('fields in Orders tables has not change', function() {
            fieldsExpectedCount('x_402400_drink_orders', '4');
        });
    });

    function fieldsExpectedCount(table, expected) {
        var gaSysDictionary = new GlideAggregate('sys_dictionary');
        gaSysDictionary.addQuery('name', '=', table);
        gaSysDictionary.addAggregate('COUNT');
        gaSysDictionary.query();
        var count = 0;
        if (gaSysDictionary.next()) {
            count = gaSysDictionary.getAggregate('COUNT');
        }
        expect(count).toBe(expected);
    }

})(outputs, steps, stepResult, assertEqual);
jasmine.getEnv().execute();
