(function(outputs, steps, stepResult, assertEqual) {

    var glideRecord = new GlideRecord('x_402400_drink_drinks');

    describe('Current user on ' + glideRecord.getClassDisplayValue() + ' table', function() {

        var defaultValue = false;

        var specificGlideElements = [{
            name: 'calories',
            create: true,
            read: true,
            write: true
        }, {
            name: 'carbohydrates',
            create: true,
            read: true,
            write: true
