            write: true
        }, {
            name: 'image',
            create: true,
            read: true,
            write: true
        }, {
            name: 'name',
            create: true,
            read: true,
            write: true
        }, {
            name: 'protein',
            create: true,
            read: true,
            write: true
        }, {
            name: 'short_description',
            create: true,
            read: true,
            write: true
        }];

        var systemGlideElements = [{
            name: 'sys_id',
            read: true,
        }, {
            name: 'sys_created_on',
            read: true,
        }, {
            name: 'sys_created_by',
            read: true,
        }, {
            name: 'sys_updated_on',
            read: true,
        }, {
            name: 'sys_updated_by',
            read: true,
        }, {
            name: 'sys_mod_count',
            read: true,
        }];

        var glideElements = [].concat(specificGlideElements, systemGlideElements);

        describe('can Create', function() {

            it('record', function() {
                checkCreate(glideRecord, true);
            });

            describe('field', function() {
                glideElements.forEach(function(glideElement) {
                    it(glideElement.name, function() {
                        checkCreate(glideRecord.getElement(glideElement.name), glideElement.create || defaultValue);
                    });
                }, this);
            });
        });

        describe('can Read', function() {

            it('record', function() {
                checkRead(glideRecord, true);
            });

            describe('field', function() {
                glideElements.forEach(function(glideElement) {
                    it(glideElement.name, function() {
                        checkRead(glideRecord.getElement(glideElement.name), glideElement.read || defaultValue);
                    });
                }, this);
            });
        });

        describe('can Write', function() {

            it('record', function() {
                checkWrite(glideRecord, true);
            });

            describe('field', function() {
                glideElements.forEach(function(glideElement) {
                    it(glideElement.name, function() {
                        checkWrite(glideRecord.getElement(glideElement.name), glideElement.write || defaultValue);
                    });
                }, this);
            });
        });

        describe('can Delete', function() {

            it('record', function() {
                checkDelete(glideRecord, true);
            });
        });
    });

    function checkCreate(object, expected) {
        var result = object.canCreate();
        expect(result).toBe(expected);
    }

    function checkDelete(object, expected) {
        var result = object.canDelete();
        expect(result).toBe(expected);
    }

    function checkRead(object, expected) {
        var result = object.canRead();
        expect(result).toBe(expected);
    }

    function checkWrite(object, expected) {
        var result = object.canWrite();
        expect(result).toBe(expected);
    }

})(outputs, steps, stepResult, assertEqual);
